import logo from './logo.svg';
import './App.css';
import {useSelector ,useDispatch} from "react-redux";
import {addcounteraction,minuscounteraction} from "./store/actions/index";


function App() {

  const dispatch = useDispatch()

  const {intialstate} = useSelector((state)=>{
    return state
  })

  const add = ()=>{
    dispatch(addcounteraction())
  }
  const sub =()=>{
dispatch(minuscounteraction())
  }
  return (

    <div className='container'>

      <h1>redux counter</h1>


      <div>
        <button onClick={add()} className=" btn btn-primary">+</button>
       {<span>{intialstate}</span>}
        <button onClick={sub()} className=" btn btn-primary">-</button>
      </div>
    </div>


  );
}

export default App;
